<?php


/**
 *  Высод списка страниц с социальных сетях
 */


if ( ! defined( 'ABSPATH' ) ) { exit; };


$socials = get_theme_mod( 'socials', false );

if ( ( $socials ) && ( ! empty( $socials ) ) ) {

    echo "<div class=\"social\">\r\n";
    echo "  <ul class=\"list-inline list-unstyled social__list list\">\r\n";

    foreach ( $socials as $slug => $value ) {
        
        if ( empty( trim( $value ) ) ) continue;

        $value = ( function_exists( 'pll__' ) ) ? pll__( $value ) : $value;

        switch ( $slug ) {

            case 'call':
                echo "<li><a href=\"tel:" . preg_replace( '~\D+~', '', esc_attr( $value ) ) . "\"><i class=\"icon icon-" . $slug . "\"></i>" . $value . "</a></li>\r\n";
                break;

            case 'envelope':
                if ( is_email( $value ) ) echo "<li><a href=\"mailto:" . sanitize_email( $value ) . "\"><i class=\"icon icon-" . $slug . "\"></i>" . $value . "</a></li>\r\n";
                break;
            
            default:
                $value = esc_url_raw( $value );
                if ( ! empty( $value )  ) echo "<li><a target=\"_blank\" href=\"" . $value . "\"><i class=\"icon icon-" . $slug . "\"></i></a></li>\r\n";
                break;
        } // switch

    } // foreach

    echo "  </ul>\r\n";
    echo "</div>\r\n";

} // if

unset( $socials );


?>